import { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'

const EditPost = () => {



    const params = useParams()
    const [post, setPost] = useState({})
    const findPostById = () =>
        fetch(`https://jsonplaceholder.typicode.com/posts/${params.id}`)
            .then(res => res.json())
            .then(p => setPost(p))
    useEffect(findPostById, [])

    console.log(post);

    const [email, setEmail] = useState(post.email);
    const [title, setTitle] = useState(post.title);
    const [body, setBody] = useState(post.body);

    const emailChangeHandler = () => {

    }

    return(
        <>
            <div className="container mt-5">
                <div className="row">
                    <div className="form-group col-8 mt-2">
                        <label htmlFor="email">Email</label>
                        <input
                            type="text" className="form-control" id="email"
                            placeholder="Email" value={email}
                            onChange={emailChangeHandler}
                        />
                    </div>

                    <div className="form-group col-8 mt-2">
                        <label htmlFor="title">Title</label>
                        <input
                            type="text" className="form-control" id="title"
                            placeholder="Title" value={title}
                        />
                    </div>
                    <div className="form-group col-8 mt-2">
                        <label htmlFor="title">Body</label>
                        <textarea
                            type="text" className="form-control" id="body"
                            placeholder="Body" value={body}
                        />
                    </div>

                    <div className="form-group col-8 mt-2">
                        <button className="btn btn-outline-primary fa-pull-right">
                            Save
                        </button>
                    </div>
                </div>
            </div>
        </>
    )
}
export default EditPost;
