import { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'

const Comment = (props) => {

    const [comments, setComments] = useState([])

    const findAllCommentsOfThisPost = (currPost) =>
        fetch(`https://jsonplaceholder.typicode.com/comments?postId=${currPost?.id}`)
            .then(res => res.json())
            .then(c => setComments(c))
    useEffect(() => findAllCommentsOfThisPost(props?.post), [])
    return (
        <>
            {
                comments.map(comment =>
                <li className="list-group-item">
                    {comment.body}
                </li>)
            }
        </>
    )
}
const UserPost = () => {
    const params = useParams()
    const [posts, setPosts] = useState({})
    const findAllPosts = () =>
        fetch(`https://jsonplaceholder.typicode.com/posts`)
            .then(res => res.json())
            .then(p => setPosts(p))
    useEffect(findAllPosts, [])
    const postsByThisUser = Object.values(posts).filter(p => p.userId === parseInt(params.id))

    const [newPost, setNewPost] = useState('')

    const postClickHandler = () => {
        fetch('https://jsonplaceholder.typicode.com/posts', {
            method: 'POST',
            body: JSON.stringify({
                title: 'foo',
                body: newPost,
                userId: parseInt(params.id),
            }),
            headers: {
                'Content-type': 'application/json; charset=UTF-8',
            },
        })
            .then((response) => response.json())
            .then((json) => setPosts([json, ...posts])
            )
    }

    const deletePostClickHandler = (item) => {
        console.log(item.id)
        fetch(`https://jsonplaceholder.typicode.com/posts/${item.id}`, {
            method: 'DELETE',
        })
            .then(response => response.json())
            .then(_post => {
                setPosts(posts.filter(i => i.id !== item.id))
            })

    }


    const [newComment, setNewComment] = useState('')
    const commentClickHandler = () => {

    }

    return (
        <>
            <details className="w-100">
                <summary>User Post</summary>
                <div>
                    {/*expand to show search bar, create comment button and post new comments*/}
                    <div className="input-group rounded">
                        <input type="search" className="form-control rounded" placeholder="Search"
                               aria-label="Search" aria-describedby="search-addon"/>
                        <span className="input-group-text border-0" id="search-addon">
                                    <i className="fas fa-search"></i>
                                  </span>
                    </div>


                    <div className="input-group rounded mt-3">
                                <textarea type="text" className="form-control rounded "
                                          placeholder="New Post..."
                                          value={newPost}
                                          onChange={(e) => setNewPost(e.target.value)}>

                                </textarea>

                        <button className="btn btn-outline-dark"
                                onClick={postClickHandler}>Post
                        </button>
                    </div>
                    <hr/>

                    <div className="rounded mt-3">
                        <ul className="list-group">
                            {
                                postsByThisUser.map(post =>
                                        <table style={{ marginBottom: '16px' }}>
                                            <tbody>
                                            <tr>
                                                <td style={{ width: '100%' }}>
                                                    <div>
                                                        <span className="fw-bold">{post.title}</span>
                                                        <i onClick={() => deletePostClickHandler(post)}
                                                           className="fas fa-times fa-pull-right"></i>
                                                    </div>
                                                    <div>{post.body}</div>
                                                </td>
                                            </tr>


                                            <tr>
                                                <div className="input-group rounded mt-3">
                                                    <textarea type="text" className="form-control rounded "
                                                              placeholder="type your comment here..."
                                                              value={newComment}
                                                              onChange={(e) => setNewComment(e.target.value)}>

                                                    </textarea>

                                                    <button className="btn btn-outline-primary"
                                                            onClick={commentClickHandler}>comment
                                                    </button>
                                                </div>

                                                <ul className="list-group">
                                                    <Comment post={post} />
                                                </ul>

                                            </tr>

                                            </tbody>
                                        </table>
                                )
                            }

                        </ul>
                    </div>


                </div>
            </details>

        </>
    )
}


export default UserPost
