import { useEffect, useState } from 'react'
import { Link, useParams } from 'react-router-dom'

const EditPost = () => {

    const params = useParams()
    const [post, setPost] = useState({})
    const findPostById = () =>
        fetch(`https://jsonplaceholder.typicode.com/posts/${params.id}`)
            .then(res => res.json())
            .then(p => setPost(p))
    useEffect(findPostById, [])

    console.log(post)


    const [title, setTitle] = useState(post.title)
    const [body, setBody] = useState(post.body)

    console.log("++", post.title)

    const saveClickHandler = () => {
        fetch(`https://jsonplaceholder.typicode.com/posts/${params.id}`, {
            method: 'PUT',
            body: JSON.stringify({
                title: title,
                body: body,
            }),
            headers: {
                'Content-type': 'application/json; charset=UTF-8',
            },
        })
            .then((response) => response.json())
            .then((json) => console.log(json));
    }

    return (
        <>
            <div className="container mt-5">
                <div className="row">


                    <div className="form-group col-8 mt-2">
                        <label htmlFor="title">Title</label>
                        <input
                            type="text" className="form-control" id="title"
                            placeholder="Title" value={title}
                            onChange={(e) => setTitle(e.target.value)}
                        />
                    </div>
                    <div className="form-group col-8 mt-2">
                        <label htmlFor="title">Body</label>
                        <textarea
                            type="text" className="form-control" id="body"
                            placeholder="Body" value={body}
                            onChange={(e) => setBody(e.target.value)}
                        />
                    </div>

                    <div className="form-group col-8 mt-2">
                        <Link to={`/details/${post.userId}`}>
                            <button className="btn btn-outline-primary fa-pull-right"
                                    onClick={saveClickHandler}>
                                Save
                            </button>
                        </Link>
                    </div>
                </div>
            </div>
        </>
    )
}
export default EditPost
