import { useEffect, useState } from 'react'
import { Link, useParams } from 'react-router-dom'
import BasicInformation from './BasicInformation'

const DetailScreen = () => {
    const params = useParams()
    // const [userDetails, setUserDetails] = useState({ 'id': 1 })
    // const findUserById = () =>
    //     fetch(`https://jsonplaceholder.typicode.com/users/${params.id}`)
    //         .then(res => res.json())
    //         .then(userDetails => setUserDetails(userDetails))
    // useEffect(findUserById, [])

    const [post, setPost] = useState({})
    const findAllPost = () =>
        fetch(`https://jsonplaceholder.typicode.com/posts`)
            .then(res => res.json())
            .then(p => setPost(p))
    useEffect(findAllPost, [])
    const postsByThisUser = Object.values(post).filter(p => p.userId === parseInt(params.id));

    const [newPost, setNewPost] = useState({})

    const deletePostClickHandler = (post) => {
        console.log(post.id);
        fetch(`https://jsonplaceholder.typicode.com/posts/${post.id}`, {
            method: 'DELETE',
        });
    }


    const [album, setAlbum] = useState('')
    const findAlbums = () =>
        fetch(`https://jsonplaceholder.typicode.com/albums/`)
            .then(res => res.json())
            .then(album => setAlbum(album))

    const albumByThisUser = Object.values(album).filter(a => a.userId === parseInt(params.id))
    useEffect(findAlbums, [])

    return (
        <>
            <div className="container">
                <div className="col-md-8">
                    <BasicInformation/>
                    {/*<div className="tab-content profile-tab" id="myTabContent">*/}
                    {/*    <ul className="nav nav-tabs mt-4 md-4" id="myTab" role="tablist">*/}
                    {/*        <h1> Basic Information</h1>*/}
                    {/*    </ul>*/}
                    {/*    <div className="tab-pane fade show active" id="home" role="tabpanel"*/}
                    {/*         aria-labelledby="home-tab">*/}

                    {/*        <div className="row">*/}
                    {/*            <div className="col-md-6">*/}
                    {/*                <label>User Id</label>*/}
                    {/*            </div>*/}
                    {/*            <div className="col-md-6">*/}
                    {/*                {userDetails.id}*/}
                    {/*            </div>*/}
                    {/*        </div>*/}
                    {/*        <div className="row">*/}
                    {/*            <div className="col-md-6">*/}
                    {/*                <label>Name</label>*/}
                    {/*            </div>*/}
                    {/*            <div className="col-md-6">*/}
                    {/*                <p>{userDetails.name}</p>*/}
                    {/*            </div>*/}
                    {/*        </div>*/}

                    {/*        <div className="row">*/}
                    {/*            <div className="col-md-6">*/}
                    {/*                <label>username</label>*/}
                    {/*            </div>*/}
                    {/*            <div className="col-md-6">*/}
                    {/*                <p>{userDetails.username}</p>*/}
                    {/*            </div>*/}
                    {/*        </div>*/}

                    {/*        <div className="row">*/}
                    {/*            <div className="col-md-6">*/}
                    {/*                <label>Email</label>*/}
                    {/*            </div>*/}
                    {/*            <div className="col-md-6">*/}
                    {/*                <p>{userDetails.email}</p>*/}
                    {/*            </div>*/}
                    {/*        </div>*/}
                    {/*        <div className="row">*/}
                    {/*            <div className="col-md-6">*/}
                    {/*                <label>Phone</label>*/}
                    {/*            </div>*/}
                    {/*            <div className="col-md-6">*/}
                    {/*                <p>{userDetails.phone}</p>*/}
                    {/*            </div>*/}
                    {/*        </div>*/}
                    {/*        <div className="row">*/}
                    {/*            <div className="col-md-6">*/}
                    {/*                <label>Website</label>*/}
                    {/*            </div>*/}
                    {/*            <div className="col-md-6">*/}
                    {/*                <p>{userDetails.website}</p>*/}
                    {/*            </div>*/}
                    {/*        </div>*/}

                    {/*        <div className="row">*/}
                    {/*            <div className="col-md-6">*/}
                    {/*                <label>Company</label>*/}
                    {/*            </div>*/}
                    {/*            <div className="col-md-6">*/}
                    {/*                <p>{userDetails?.company?.name}</p>*/}
                    {/*            </div>*/}
                    {/*        </div>*/}

                    {/*        <div className="row">*/}
                    {/*            <div className="col-md-6">*/}
                    {/*                <label>Address</label>*/}
                    {/*            </div>*/}
                    {/*            <div className="col-md-6">*/}
                    {/*                <p>{userDetails?.address?.street}, {userDetails?.address?.suite}, {userDetails?.address?.city}</p>*/}

                    {/*            </div>*/}
                    {/*        </div>*/}

                    {/*        <div className="row">*/}
                    {/*            <div className="col-md-6">*/}
                    {/*                <label>ZipCode</label>*/}
                    {/*            </div>*/}
                    {/*            <div className="col-md-6">*/}
                    {/*                <p>{userDetails?.address?.zipcode}</p>*/}
                    {/*            </div>*/}
                    {/*        </div>*/}
                    {/*    </div>*/}
                    {/*</div>*/}


                    <details className="w-100">
                        <summary>User Post</summary>
                        <div>
                            {/*expand to show search bar, create comment button and post new comments*/}
                            <div className="input-group rounded">
                                <input type="search" className="form-control rounded" placeholder="Search"
                                       aria-label="Search" aria-describedby="search-addon"/>
                                <span className="input-group-text border-0" id="search-addon">
                                    <i className="fas fa-search"></i>
                                  </span>
                            </div>


                            <div className="input-group rounded mt-3">
                                <textarea type="text" className="form-control rounded "
                                          placeholder="type your comment here..."
                                          value={newPost}
                                          onChange={(e) => setNewPost(e.target.value)}>

                                </textarea>

                                <button className="btn btn-outline-dark">Post</button>
                            </div>

                            <div className="rounded mt-3">
                                <ul className="list-group">
                                    {
                                        postsByThisUser.map(post =>
                                            <table style={{marginBottom: '16px'}}>
                                                <tbody>
                                                <tr>
                                                    <td style={{width: "100%"}}>
                                                        <div>
                                                            <span className="fw-bold">{post.title}</span>
                                                            <i onClick={() => deletePostClickHandler(post)}
                                                               className="fas fa-times fa-pull-right"></i>
                                                        </div>
                                                        <div>{post.body}</div>
                                                    </td>
                                                </tr>
                                                </tbody>
                                            </table>
                                        )
                                    }

                                </ul>
                            </div>


                        </div>
                    </details>


                    {/*expand to show album list, on click each album to show photos*/}
                    <details className="w-100">
                        <summary>User Album</summary>
                        <div>
                            <ul>
                                {
                                    albumByThisUser.map(album =>
                                        <li>
                                            <Link to={`/album/${album.id}`}>
                                                {album.title}
                                            </Link>
                                        </li>
                                    )
                                }
                            </ul>

                        </div>
                    </details>


                </div>
            </div>


        </>
    )

}
export default DetailScreen
