import { useEffect, useState } from 'react'
import { Link, useParams } from 'react-router-dom'

const PhotoScreen = () => {
    const params = useParams();
    const [photo, setPhoto] = useState([]);
    const findAllPhotos = () =>
        fetch('https://jsonplaceholder.typicode.com/photos')
            .then(res => res.json())
            .then(photo => setPhoto(photo))
    useEffect(findAllPhotos, []);
    console.log("**", photo)

    const photoFromOneAlbum = [];
    for(let i = 0; i < photo.length; i++){
        if(photo[i].albumId === parseInt(params.id)){
            console.log(photo[i].title);
            photoFromOneAlbum.push(photo[i]);
        }
    }
    // const photoFromOneAlbum = Object.values(photo).filter(p => p.albumId === params.id);
    console.log(photoFromOneAlbum, "+++", params.id);
    return (
        <>
            <div  className="row mt-2">
                {
                    photoFromOneAlbum.map(photo => {
                            return (
                                <>
                                    <div className="col-md-3 mb-4">
                                        <div className="card h-100 text-center p-4" key={photo.id}>
                                            <img className="card-img-top" src={photo.url} alt={photo.title} height="250px"/>
                                            <div className="card-body">
                                                <h5 className="card-title mb-0">{photo.title.substring(0,12)}...</h5>
                                                {/*<p className="card-text lead fw-bolder">*/}
                                                {/*    ${photo.price}*/}
                                                {/*</p>*/}


                                                {/*<Link to={`/photos/${photo.id}`}*/}
                                                {/*      className="btn btn-outline-dark">*/}
                                                {/*    More*/}
                                                {/*</Link>*/}

                                            </div>
                                        </div>
                                    </div>
                                </>
                            )
                        }
                    )
                }
            </div>
        </>
    )

}
export default  PhotoScreen;
